import React, { useState } from "react";
import {
  LightBulbIcon,
  DocumentTextIcon,
  BookOpenIcon,
  AcademicCapIcon,
  SparklesIcon,
  ClipboardDocumentIcon,
} from "@heroicons/react/24/outline";

const AIFeatures: React.FC = () => {
  const [activeTab, setActiveTab] = useState("summarize");
  const [content, setContent] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const features = [
    {
      id: "summarize",
      name: "Note Summarization",
      description: "Transform lengthy notes into concise summaries",
      icon: DocumentTextIcon,
      color: "text-blue-600",
      bgColor: "bg-blue-100",
    },
    {
      id: "flashcards",
      name: "Flashcard Generation",
      description: "Create study flashcards from your notes",
      icon: BookOpenIcon,
      color: "text-purple-600",
      bgColor: "bg-purple-100",
    },
    {
      id: "assignment",
      name: "Assignment Help",
      description: "Get guidance on essays and research papers",
      icon: AcademicCapIcon,
      color: "text-green-600",
      bgColor: "bg-green-100",
    },
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!content.trim()) return;

    setLoading(true);
    setResult("");

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("Authentication required");
      }

      let endpoint = "";
      let requestBody: any = { content };

      switch (activeTab) {
        case "summarize":
          endpoint = "/api/ai/summarize";
          requestBody = { 
            content, 
            style: "concise" 
          };
          break;
        case "flashcards":
          endpoint = "/api/ai/flashcards";
          requestBody = { 
            content, 
            count: 5, 
            difficulty: "medium" 
          };
          break;
        case "assignment":
          endpoint = "/api/ai/assignment";
          requestBody = { 
            content, 
            type: "essay" 
          };
          break;
        default:
          throw new Error("Invalid feature");
      }

      const response = await fetch(endpoint, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to process request");
      }

      const data = await response.json();

      if (activeTab === "summarize") {
        setResult(data.summary);
      } else if (activeTab === "flashcards") {
        setResult(JSON.stringify(data.flashcards, null, 2));
      } else if (activeTab === "assignment") {
        setResult(data.help);
      }
    } catch (error: any) {
      setResult(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">AI Features</h1>
        <p className="text-gray-600">
          Leverage AI to enhance your academic productivity
        </p>
      </div>

      {/* Feature Tabs */}
      <div className="card">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {features.map((feature) => (
              <button
                key={feature.id}
                onClick={() => setActiveTab(feature.id)}
                className={`py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === feature.id
                    ? "border-primary-500 text-primary-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                <div className="flex items-center">
                  <feature.icon className="w-5 h-5 mr-2" />
                  {feature.name}
                </div>
              </button>
            ))}
          </nav>
        </div>

        <div className="mt-6">
          {activeTab === "summarize" && (
            <div className="space-y-4">
              <div className="flex items-center">
                <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center mr-3">
                  <DocumentTextIcon className="w-5 h-5 text-blue-600" />
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">
                    Note Summarization
                  </h3>
                  <p className="text-sm text-gray-600">
                    Transform lengthy notes into concise, organized summaries
                  </p>
                </div>
              </div>

              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Your Notes
                  </label>
                  <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    rows={8}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    placeholder="Paste your notes here..."
                  />
