                    <select className="px-3 py-1 border border-gray-300 rounded-md text-sm">
                      <option>Concise</option>
                      <option>Detailed</option>
                      <option>Bullet Points</option>
                      <option>Study Guide</option>
                    </select>
                  </div>
                  <button
                    type="submit"
                    disabled={loading || !content.trim()}
                    className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? (
                      <div className="flex items-center">
                        <div className="spinner mr-2"></div>
                        Processing...
                      </div>
                    ) : (
                      <div className="flex items-center">
                        <SparklesIcon className="w-4 h-4 mr-2" />
                        Generate Summary
                      </div>
                    )}
                  </button>
                </div>
              </form>

              {result && (
                <div className="mt-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    AI Summary
                  </label>
                  <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm text-gray-500">
                        Generated with GPT-4
                      </span>
                      <button className="text-primary-600 hover:text-primary-500 text-sm">
                        <ClipboardDocumentIcon className="w-4 h-4" />
                      </button>
                    </div>
                    <p className="text-gray-900 whitespace-pre-wrap">
                      {result}
                    </p>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === "flashcards" && (
            <div className="space-y-4">
              <div className="flex items-center">
                <div className="w-10 h-10 bg-purple-100 rounded-lg flex items-center justify-center mr-3">
                  <BookOpenIcon className="w-5 h-5 text-purple-600" />
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">
                    Flashcard Generation
                  </h3>
                  <p className="text-sm text-gray-600">
                    Create study flashcards from your notes
                  </p>
                </div>
              </div>

              <div className="text-center py-12 text-gray-500">
                <BookOpenIcon className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                <p>Flashcard generation feature coming soon!</p>
              </div>
            </div>
          )}

          {activeTab === "assignment" && (
            <div className="space-y-4">
              <div className="flex items-center">
                <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center mr-3">
                  <AcademicCapIcon className="w-5 h-5 text-green-600" />
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">
                    Assignment Help
                  </h3>
                  <p className="text-sm text-gray-600">
                    Get guidance on essays and research papers
                  </p>
                </div>
              </div>

              <div className="text-center py-12 text-gray-500">
                <AcademicCapIcon className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                <p>Assignment help feature coming soon!</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Usage Info */}
      <div className="card bg-blue-50 border-blue-200">
        <div className="flex items-start">
          <LightBulbIcon className="w-6 h-6 text-blue-600 mt-0.5 mr-3" />
          <div>
            <h3 className="font-medium text-blue-900">AI Feature Usage</h3>
            <p className="text-sm text-blue-700 mt-1">
              You have used 2 out of 5 AI summaries this month. Upgrade to
              Premium for unlimited access.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AIFeatures;
